import { Button, Drawer, Form, Input } from "antd";
import FormItemInput from "antd/es/form/FormItemInput";
import React, { PureComponent } from "react";
import { userDetails } from "./UserDetails/UserDetails";

export default class AddUser extends PureComponent {
  onFinish = (values) => {
    console.log(values);
  };
  render() {
    return (
      <Drawer
        placement="right"
        title="Add User"
        open={this.props.openDrawer}
        onClose={this.props.toggleDrawer}
      >
        <Form layout="vertical" onFinish={this.onFinish}>
          {this.props.userDetails.map((detail) => {
            return (
              <Form.Item label={detail.key}>
                <Input
                  placeholder={detail.value}
                  onChange={this.props.updateDetails(value, detail.key)}
                />
              </Form.Item>
            );
          })}
          <Form.Item>
            <Button type="primary" onClick={this.props.onFormSubmit()}>
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Drawer>
    );
  }
}
