import React, { PureComponent } from "react";
import { AddButton, Counter } from "./Components/index";

export default class App1 extends PureComponent {
  constructor() {
    super();
    this.state = {
      counter: 0,
      todoList: [],
      userList: [],
    };
  }

  incrementCounter = () => {
    let newCounter = this.state.counter + 1;
    this.setState({ counter: newCounter });
  };

  addTodo = (todo) => {};

  addUser = (user) => {
    let updatedUserList = this.state.userList;
    updatedUserList.push(user);
    this.setState({ userList: updatedUserList });
  };

  render() {
    return (
      <div>
        <AddButton incrementCounter={this.incrementCounter} />
        <Counter counter={this.state.counter} />
      </div>
    );
  }
}
